import React, { Component } from 'react';
import axios from 'axios';
import Suggestions from './Suggestions';

//const { API_KEY } = process.env
const API_URL = 'https://www.googleapis.com/customsearch/v1?key=AIzaSyBZbwQOnwtlio5EcxyTEAEehgXPlprGJL8&cx=008534924047867193897:vux-_cu2oxi&q={query in the search box}&start=1';

class Search extends Component {
    constructor(props) {
        super(props);
        this.state = {
            query: '',
            results: []
          }
    }


