import React, { Component } from 'react';
// import Suggestions from './Suggestions';

class Search extends Component {
    constructor(props) {
        super(props);
        this.state = {
            query: '',
            totalItems: '',
            results: '',
            data: ''
        }
    }

    componentDidMount() {
        fetch('https://www.googleapis.com/customsearch/v1?key=AIzaSyBZbwQOnwtlio5EcxyTEAEehgXPlprGJL8&cx=008534924047867193897:vux-_cu2oxi&q={query in the search box}&start=1')
            .then(response => response.json())
            .then(json => {
                console.log(json);
                this.setState({ results: json });
                this.setState({ totalItems: this.state.results.searchInformation.totalResults });
                this.setState({ data: this.state.results.items })
            }
            );
    }
    getInfo = () => {
        console.log(this.state.results);
        fetch('https://www.googleapis.com/customsearch/v1?key=AIzaSyBZbwQOnwtlio5EcxyTEAEehgXPlprGJL8&cx=008534924047867193897:vux-_cu2oxi&q={query in the search box}&start=1')
            .then(response => response.json())
            .then(json => {
                console.log(json);
                this.setState({ results: json });
            }
            );

    }

    handleInputChange = () => {
        this.setState({
            query: this.search.value
        }, () => {
            if (this.state.query && this.state.query.length > 1) {
                if (this.state.query.length % 2 === 0) {
                    this.getInfo()
                }
            } else if (!this.state.query) {
            }
        })
    }

    render() {

        console.log(this.state.results);
        const postItem = this.state.data.map(item => {
            return <li key={'kind'}>{item[0]}</li>;
        })
        console.log(this.state.totalItems);
        return (
            <form className="formAlighn">
                <div className="row">
                    <div className="col-md-12 col-xs-12">
                        {this.state.totalItems}  Result
        </div>
                </div>
                <div className="row">
                    <div className="col-md-12 col-xs-12">
                        <input className="searchBar"
                            placeholder="Search for..."
                            ref={input => this.search = input}
                            onChange={this.handleInputChange}
                        />
                    </div>
                    {postItem}
                </div>


                {/* <Suggestions results={this.state.results} /> */}
            </form>
        )
    }
}

export default Search
