import React, { Component } from 'react';
class Search extends Component {
    token = null;
    state = {
        query: "",
        people: []
    };

    onChange = e => {
        const { value } = e.target;
        this.setState({
            query: value
        });

        this.search(value);
    };

    search = query => {
        const url = `https://jsonplaceholder.typicode.com/posts`;
        const token = {};
        this.token = token;

        fetch(url)
            .then(results => results.json())
            .then(data => {
                if (this.token === token) {
                    this.setState({ people: data.results });
                }
            });
    };

    componentDidMount() {
        this.search("");
    }

    render() {
        let postItem;
        if (this.state.people) {
          postItem = this.state.people.map(person => {
            return (
                <ul key={person.id}>
                <li>{person.title}</li>
            </ul>
            )
          });
        }
        return (
            <form>
                <input
                    type="text"
                    className="search-box"
                    placeholder="Search for..."
                    onChange={this.onChange}
                />
                {postItem}
            </form>
        );
    }
}
export default Search;
