import React, { Component } from 'react';
// import Suggestions from './Suggestions';

class Search extends Component {
    constructor(props) {
        super(props);
        this.state = {
            query: '',
            totalItems:'',
            results: ''
          }
    }

componentDidMount() {
    fetch('https://www.googleapis.com/customsearch/v1?key=AIzaSyBZbwQOnwtlio5EcxyTEAEehgXPlprGJL8&cx=008534924047867193897:vux-_cu2oxi&q={query in the search box}&start=1')
      .then(response => response.json())
      .then(json => {
          console.log(json);
        this.setState({ results: json });
        this.setState({totalItems: this.state.results.searchInformation.totalResults});
      }
      );
  }
  getInfo = () => {
    console.log(this.state.results);
    fetch('https://www.googleapis.com/customsearch/v1?key=AIzaSyBZbwQOnwtlio5EcxyTEAEehgXPlprGJL8&cx=008534924047867193897:vux-_cu2oxi&q={query in the search box}&start=1')
    .then(response => response.json())
    .then(json => {
        console.log(json);
      this.setState({ results: json });
    }
    );

  }

  handleInputChange = () => {
    this.setState({
      query: this.search.value
    }, () => {
      if (this.state.query && this.state.query.length > 1) {
        if (this.state.query.length % 2 === 0) {
          this.getInfo()
        }
      } else if (!this.state.query) {
      }
    })
  }

  render() {
      const data= this.state.results.items
      console.log(this.state.results.items);

    //   const postItem = this.state.results.items.map(post =>{
    //       <div key= {post.kind}>
    //       <h3>{post.title}</h3>
    //       {/* <p>{post.boddy}</p> */}
    //       </div>
    //   });
    //   this.setState({totalItems :this.state.results.searchInformation.totalResults });
    //   console.log("render");
    // this.totalItems = this.state.results.searchInformation.totalResults;
       console.log(this.state.totalItems);
    return (
      <form className="formAlighn">
      <div className="row">
        <div className="col-md-12 col-xs-12">
            {this.state.totalItems}  Result
        </div>
      </div>
      <div className="row">
      <div className="col-md-12 col-xs-12">
      <input className="searchBar"
          placeholder="Search for..."
          ref={input => this.search = input}
          onChange={this.handleInputChange}
        />
        </div>
        {/* {postItem} */}
        <ul>
        {data.map(function(e){
                    return <li>{e.kind}</li>
                })}
      {data.map(item => {
        return <li>{item[0]}</li>;
      })}
    </ul>
      </div>


        {/* <Suggestions results={this.state.results} /> */}
      </form>
    )
  }
}

export default Search
