import React, { Component } from 'react';
import Post from './components/post';
import Search from './components/search';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      totalPost: '',
    }

  }
  componentWillMount() {
    fetch('https://jsonplaceholder.typicode.com/posts')
      .then(res => res.json())
      .then(data => this.setState({ posts: data, totalPost: data.length }));
  }

  chnage=(x)=>{
    console.log("hello");
    var wantedData = this.state.posts.filter(function(i) {
      return i.id === x;
    });
    this.setState({posts: wantedData});
    console.log(this.state.posts)
  }
  render() {
    return (
      <div className="app row">
        <div className="col-md-12 col sm-12">
          {this.state.totalPost} Results
       <Search onSearchChange={this.chnage}/>
          <hr />
          <Post data={this.state.posts} />
        </div>

      </div>
    );
  }
}

export default App;
