import React, { Component } from 'react';
import Post from './components/post';
import Search from './components/search';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      totalPost: '',
      result:[],
      lang:''
    }

  }
  componentWillMount() {
    fetch('https://jsonplaceholder.typicode.com/posts')
      .then(res => res.json())
      .then(data => this.setState({ posts: data, totalPost: data.length }));
  }

  handSearchChange = () =>{
    this.setState ({ lang: this.input.value}) ;
    var wantedData = this.state.posts.filter(function(i) {
      return i.id === this.input.value;
    });
    this.setState({posts:wantedData})
    console.log(this.state.result);
  }
  render() {

    return (
      <div className="app row">
        <div className="col-md-12 col sm-12">
        <input ref={(ref) => this.input = ref} className="searchBar" type="text" placeholder="search" onChange={this.handSearchChange} />
          {this.state.totalPost} Results
       <Search />
          <hr />
          <Post data={this.state.posts} />
        </div>

      </div>
    );
  }
}

export default App;
