import React, { Component } from 'react'

function searchingFor(term){
    return function(x){
      return x.title.includes(term) || !term;
    }
  }

class PostItem extends Component {

    render() {
        return (
            <div>
                {this.props.data.filter(searchingFor(this.props.termValue)).map(function(person){
          return (
            <div>
            <div key={person.id}>
                <h3>{person.title}</h3>
                <p>{person.body}</p>
                <hr />
            </div>
            </div>
          )
        })}
            </div>
            // <div key={this.props.post.id}>
            //     <h3>{this.props.post.title}</h3>
            //     <p>{this.props.post.body}</p>
            //     <hr />
            // </div>
        )
    }
}
export default PostItem;
