          <input type="text" 
          value={this.state.term}
          onChange={this.searchHandler}/>
        </form> */}
        <PostItem data={this.state.posts} termValue={this.state.term}/>
        {/* {this.state.posts.filter(searchingFor(this.state.term)).map(function(person){
          return (
            <div>
            <div key={person.id}>
                <h3>{person.title}</h3>
                <p>{person.body}</p>
                <hr />
            </div>
            </div>
          )
        })} */}
