import React, { Component } from 'react';
import Post from './components/post';
import Search from './components/search';
import './App.css';

class App extends Component {
  constructor(props){
    super(props);
    this.state ={
      posts :[],
      totalPost:'',
      searchValue:''
    }

  }
  onloadFetch(){
    fetch('https://jsonplaceholder.typicode.com/posts')
    .then(res => res.json())
    .then(data => this.setState({posts:data , totalPost: data.length}));
  }

  handleSearch = (searchValue) => {
    var filtered = this.state.posts.filter(a=>a.type===searchValue);
    this.setState({posts: filtered});
}
  render() { 
    return (
     <div className="app">
       {this.state.totalPost} Results
       <Search onSearchChange={this.handleSearch}/>
       <hr />
       <Post data={this.state.posts}/>
     </div>
    );
  }
}

export default App;
